)
values(
#{form.materialId} ,#{form.lastInTime} ,#{form.lastInQuantity} ,#{param1.createUid} ,#{param1.createTime} ,
#{form.warehouseId} ,#{form.reservoirId} ,#{form.stockId}
)

mapper.update:
