quantity = #{form.quantity} , last_in_time = #{param1.lastInTime} , last_out_time = #{form.lastOutTime} , last_in_quantity = #{param1.lastInQuantity} , last_out_quantity = #{form.lastOutQuantity}
where id = #{param1.id}
